INSERT INTO `faq` (`id`, `tema`, `tanya`, `jawab`, `faq_tag`, `foto`) VALUES
(1, 'Kearsipan', 'Apa yang dimaksud dengan arsip?', 'Berdasarkan UU Nomor 43 Tahun 2009, arsip merupakan rekaman kegiatan atau peristiwa dalam berbagai bentuk dan media sesuai perkembangan teknologi informasi dan komunikasi, yang dibuat serta diterima oleh lembaga negara, pemerintah daerah, lembaga pendidikan, perusahaan, organisasi politik, organisasi kemasyarakatan dan perseorangan dalam pelaksanaan kehidupan berbangsa dan bernegara.', 'arsip, pengertian, dasar', 'definisi_arsip.jpg'),
(2, 'Kearsipan', 'Apa saja pembagian arsip itu?', 'Penggolongan arsip berdasarkan fungsi dan kegunaannya, arsip dibedakan menjadi 2 macam, yaitu arsip dinamis dan arsip statis. Adalah arsip yang digunakan secara langsung dalam kegiatan organisasi/perkantoran sehari-hari. Arsip statis adalah arsip yang dihasilkan oleh pencipta arsip karena memiliki nilai guna kesejarahan, telah habis retensinya, dan berketerangan dipermanenkan yang telah diverifikasi baik secara langsung maupun tidak langsung oleh Arsip Nasional Republik Indonesia dan/atau lembaga kearsipan', 'arsip, dinamis, statis', 'jenis_arsip.jpg'),
(3, 'Kearsipan', 'Apa saja pengelolaan arsip dinamis itu?', 'Pengelolaan arsip dinamis dimulai dari penciptaan arsip, penggunaan arsip, pemeliharaan arsip dan penyusutan arsip.', 'siklus, dinamis, pengelolaan', 'siklus_arsip.jpg'),
(4, 'Pemusnahan Arsip', 'Apakah dalam pemusnahan suatu arsip tekstual, arsip digitalnya juga ikut dimusnahkan?', 'Menurut Peraturan Pemerintah Nomor 28 Tahun 2012, pemusnahan arsip dilakukan secara total sehingga fisik dan informasi arsip musnah dan tidak dapat dikenali. Arsip hasil digitisasi merupakan bagian dari informasi suatu arsip tekstual sehingga apabila arsip tekstual itu dimusnahkan, arsip digitalnya juga harus dimusnahkan.', 'musnah, pemusnahan, digitalisasi', 'pemusnahan.jpg');
