(599, 'KU0', 'KU0.0', 'KU0.0.0', 'KU0.0.0', 'KU000', 'tersier', 'Rencana Kerja Anggaran Kementerian/Lembaga (RKA-K/L) Kementerian Keuangan', 'Arsip yang berkaitan dengan penetapan RKA-K/L Kementerian Keuangan.', NULL, 'Terbuka', 'Umum/Publik', 1, 4, 'musnah', NULL),
(600, 'KU0', 'KU0.0', 'KU0.0.1', 'KU0.0.1', 'KU001', 'tersier', 'Penyusunan RKA-K/L Kementerian Keuangan', 'Arsip yang berkaitan dengan penyusunan RKA-K/L Kementerian Keuangan; seperti:', 'review baseline;\r\npagu indikatif; pagu anggaran; dan pagu alokasi anggaran;\r\npersetujuan multiyears contract (kontrak tahun jamak);\r\nrevisi anggaran;\r\nusulan premi/insentif cukai;\r\nusulan standar biaya masukan/satuan biaya masukan lainnya/standar biaya keluaran; dan\r\ntarget pagu penggunaan penerimaan negara bukan pajak.', 'Terbatas', 'Eselon IV', 1, 4, 'musnah', NULL),
(601, 'KU0', 'KU0.1', NULL, 'KU0.1', 'KU01', 'sekunder', 'Penganggaran pada Unit Organisasi Lingkup Kementerian Keuangan', 'Arsip yang berkaitan dengan penyiapan bahan dan penyusunan anggaran pendapatan dan belanja Unit Organisasi Lingkup Kementerian Keuangan; antara lain:', 'review baseline;\r\npagu indikatif; pagu anggaran; dan pagu alokasi anggaran;\r\npersetujuan multiyears contract (kontrak tahun jamak);\r\nrevisi anggaran;\r\nusulan premi/insentif;\r\nusulan standar biaya masukan/satuan biaya masukan lainnya/standar biaya keluaran; dan\r\ntarget pagu penggunaan penerimaan negara bukan pajak.', 'Terbatas', 'Eselon IV', 1, 4, 'musnah', NULL),
