(628, 'OT2', 'OT2.2', NULL, 'OT2.2', 'OT22', 'sekunder', 'Kompetensi Jabatan', 'Arsip yang berkaitan dengan penyusunan kamus kompetensi jabatan di lingkungan Kementerian.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(629, 'OT2', 'OT2.2', 'OT2.2.0', 'OT2.2.0', 'OT220', 'tersier', 'Kamus Kompetensi Teknis', 'Arsip yang berkaitan dengan penyusunan kamus kompetensi teknis di lingkungan Kementerian Keuangan seperti:', 'pemetaan urusan; sub urusan; fungsi utama dan unit kompetensi;\r\nkonsep kamus kompetensi teknis;\r\nnaskah dinas terkait reviu/pembahasan/hasil pembahasan/finalisasi kamus kompetensi teknis;\r\nusulan kamus kompetensi teknis dari Menteri Keuangan kepada Menteri PAN-RB;\r\nnaskah dinas persetujuan/penolakan kamus kompetensi teknis dari Menteri PAN-RB;\r\nnaskah dinas terkait pemrosesan penetapan RKMK mengenai kamus kompetensi teknis; dan\r\nKMK mengenai kamus kompetensi teknis.', 'Terbatas', 'Eselon IV', 2, 3, 'musnah', NULL),
(630, 'OT2', 'OT2.2', 'OT2.2.1', 'OT2.2.1', 'OT221', 'tersier', 'Standar Kompetensi Jabatan', 'Arsip yang berkaitan dengan penyusunan Standar Kompetensi Jabatan di lingkungan Kementerian Keuangan; seperti:', 'naskah dinas terkait usulan dari unit kerja; hasil reviu/ pembahasan/ hasil pembahasan/ finalisasi Standar Kompetensi Teknis Jabatan (SKTJ); dan\r\nnaskah dinas terkait usulan Standar Kompetensi Jabatan (SKJ) dari unit kerja sampai dengan penetapan KMK.', 'Terbatas', 'Eselon IV', 2, 3, 'musnah', NULL),
