(654, 'PB0', 'PB0.2', 'PB0.2.2', 'PB0.2.2', 'PB022', 'tersier', 'Reviu Pelaksanaan Anggaran Kantor Wilayah Ditjen Perbendaharaan', 'Arsip yang berkaitan dengan kegiatan reviu pelaksanaan anggaran Kantor Wilayah Ditjen Perbendaharaan.', NULL, 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(655, 'PB1', NULL, NULL, 'PB1', 'PB1', 'primer', 'Pengelolaan Kas Negara', 'Arsip yang berkaitan dengan kegiatan Kementerian Keuangan berupa pengelolaan kas negara.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(656, 'PB1', 'PB1.0', NULL, 'PB1.0', 'PB10', 'sekunder', 'Optimalisasi Kas', 'Arsip yang berkaitan dengan optimalisasi kas; seperti:', 'penyusunan strategi likuiditas;\r\nperencanaan portofolio investasi surat berharga negara;\r\noptimalisasi kas dengan penempatan dana pada counterparty; dan\r\npemenuhan kekurangan kas dengan transaksi penarikan atas penempatan dana pada counterparty (Bank Umum).', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(657, 'PB1', 'PB1.1', NULL, 'PB1.1', 'PB11', 'sekunder', 'Penempatan Dana Likuid/Kebijakan Treasury Dealing Room (TDR)', 'Arsip yang berkaitan dengan kebijakan penempatan dana likuid; seperti:', 'petunjuk teknis; laporan hasil pemeriksaan operasional treasury dealing room;\r\ntreasury dealing room dengan mitra kerjanya;\r\nanalisis mark to market atas portofolio treasury dealing room;\r\npengelolaan portofolio;\r\npengelolaan terkait Bank Umum Mitra Penempatan Utang Negara (BUMPUN);\r\npenetapan portfolio pengelolaan kas;\r\npenyelenggaraan rapat Asset-Liabbility Committee (ALCO) dan data posisi portofolio aset dan kewajiban pemerintah; dan\r\ndata perencanaan kas.', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(658, 'PB1', 'PB1.2', NULL, 'PB1.2', 'PB12', 'sekunder', 'Manajemen Penerimaan dan Pengeluaran Kas', 'Arsip yang berkaitan dengan manajemen penerimaan dan pengeluaran kas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(659, 'PB1', 'PB1.2', 'PB1.2.0', 'PB1.2.0', 'PB120', 'tersier', 'Penetapan Bank Persepsi dan Kanal Pembayaran; Bank Operasional dan Bank Penyalur Gaji', 'Arsip yang berkaitan dengan penetapan bank persepsi dan kanal pembayaran; bank operasional dan bank penyalur gaji; seperti:', 'penunjukan bank operasional;\r\npenetapan bank penyalur gaji;\r\nizin menjadi bank/pos persepsi; dan\r\npengumuman bank umum yang menjadi mitra pemerintah dalam pengelolaan rekening milik kementerian negara/ lembaga/ satuan kerja.', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(660, 'PB1', 'PB1.2', 'PB1.2.1', 'PB1.2.1', 'PB121', 'tersier', 'Pengelolaan Bank Persepsi dan Kanal Pembayaran; Bank Operasional dan Bank Penyalur Gaji', 'Arsip yang berkaitan dengan pengelolaan dan pembinaan bank persepsi dan kanal pembayaran; bank operasional; dan bank penyalur gaji; seperti:', 'surat permohonan dari bank umum untuk menjadi bank operasional;\r\nsurat keputusan pemberhentian sementara (suspend) penyaluran dana SP2D pada bank operasional dan pembukaan suspend; surat pemberitahuan dari bank operasional tentang gangguan interkoneksi;\r\nevaluasi serta pelaporan kinerja bank operasional; bank/ pos persepsi dan lembaga keuangan lainnya; dan\r\nsurat pemberhentian/ pencabutan ijin sebagai bank/pos persepsi; peraturan terkait penerimaan negara melalui bank/ pos persepsi; laporan hasil pembinaan terhadap bank/ pos persepsi; dan permintaan pemberhentian ijin sebagai bank/ pos persepsi.', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(661, 'PB1', 'PB1.2', 'PB1.2.2', 'PB1.2.2', 'PB122', 'tersier', 'Pengelolaan Penerimaan dan Pengeluaran Kas', 'Arsip yang berkaitan dengan manajemen penerimaan dan pengeluaran kas; seperti:', 'kertas kerja monitoring saldo kas rekening pengeluaran bank operasional;\r\npermintaan tambahan dropping dana dan hasil rekonsiliasi data dan dana dan atau email permintaan tambahan dana bank operasional/ bank penyalur gaji;\r\npenyusunan pengembalian dan surat keputusan pembayaran perhitungan pihak ketiga; dan\r\njawaban atas permasalahan terkait konsolidasi rekening dan dukungan teknis.', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(662, 'PB1', 'PB1.2', 'PB1.2.3', 'PB1.2.3', 'PB123', 'tersier', 'Pedoman Pelaksanaan Langkah-langkah akhir Tahun Anggaran', 'Arsip yang berkaitan dengan Manajemen pengelolaan dan penyusunan pedoman langkah-langkah akhir tahun anggaran.', NULL, 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(663, 'PB1', 'PB1.2', 'PB1.2.4', 'PB1.2.4', 'PB124', 'tersier', 'Evaluasi Pengelolaan Penerimaan dan Pengeluaran Kas', 'Arsip yang berkaitan dengan evaluasi pengelolaan penerimaan dan pengeluaran kas; seperti:', 'laporan penatausahaan rekening penerimaan dan rekening retur KPPN; laporan rekening penerimaan dan rekening retur dari KPPN; konsep /net laporan penatausahaan rekening penerimaan dan rekening retur KPPN; dan peraturan terkait rekening penerimaan dan rekening retur;\r\nlaporan SP2D reject yang telah di-approve; data SP2D reject; CMS portal BO I Pusat pada SPAN; laporan SP2D reject yang telah di-approve; dan peraturan terkait penyelesaian SP2D reject; dan\r\n\r\n\r\nlaporan buku biru; laporan kas pemerintah pada SPAN', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(664, 'PB1', 'PB1.3', NULL, 'PB1.3', 'PB13', 'sekunder', 'Manajemen Kas Pinjaman dan Hibah', 'Arsip yang berkaitan dengan manajemen kas pinjaman dan hibah; seperti:', 'pembukaan rekening khusus;\r\npenarikan pinjaman tunai/ program dan penarikan pinaman dan hibah melalui mekanisme rekening khusus;\r\npenyampaian withdrawal application dalam rangka initial deposit; replenishment; reimbursement; dan pembiayaan pendahuluan kepada pemberi pinjaman atau hibah;\r\npemindahbukuan/ debit dari rekening khusus (reksus) ke rekening Bendahara Umum Negara (BUN); laporan pembebanan reksus; dan SP2D reksus;\r\npenggantian dana rekening kas umum negara (RKUN) dari reksus terkait atas diterbitkannya SP2D reksus; daftar transaksi melalui aplikasi SPAN;\r\npenatausahaan rekening pinjaman dan hibah termasuk surat berharga negara;\r\ndaftar surat perintah debet dan warkat pembebanan rekening;\r\nlaporan kas posisi; laporan arus kas; dan neraca; catatan atas laporan keuangan;\r\ndaftar aplikasi penarikan dana;\r\nberita acara rekonsiliasi; dan\r\nlaporan monitoring hibah langsung', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(665, 'PB1', 'PB1.4', NULL, 'PB1.4', 'PB14', 'sekunder', 'Manajemen Rekening Lainnya dan Pembinaan Pertanggungjawaban Bendahara', 'Arsip yang berkaitan dengan kegiatan manajemen rekening lainnya dan pembinaan pertanggungjawaban bendahara; seperti:', 'surat penyaluran rekening lainnya sumber daya alam dan non sumber daya alam; dan surat permintaan pemindahbukuan dana ntuk rekening lainnya sumber daya alam dan non sumber daya alam dari Direktorat Jenderal Anggaran;\r\nlaporan pemantauan saldo rekening lainnya sumber daya alam dan non sumber daya alam;\r\nlaporan kas posisi rekening lainnya sumber daya alam dan non sumber daya alam;\r\nberita acara rekonsiliasi data rekening lainnya sumber daya alam dan non sumber daya alam dengan pihak eksternal;\r\nsurat persetujuan/ penolakan permohonan pembukaan rekening lainnya;\r\nsurat pemblokiran; pengaktifan kembali; dan penutupan rekening lainnya milik K/L;\r\nevaluasi; monitoring; dan pemantauan saldo rekening pemerintah lainnya milik K/L;\r\npengelolaan dana treasury national pooling di bank umum; dan\r\nlaporan rekapitulasi LPJ bendahara per bagian anggaran tingkat nasional; konsep /net laporan rekapitulasi LPJ bendahara per bagian anggaran tingkat nasional; rekapitulasi LPJ bendahara per bagian anggaran tingkat wilayah; peraturan terkait kedudukan dan tangggung jawab bendahara pada satuan kerja pengelola APBN.', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(666, 'PB1', 'PB1.5', NULL, 'PB1.5', 'PB15', 'sekunder', 'Setelmen; Akuntansi dan pelaporan kas', 'Arsip yang berkaitan setelemen; akuntansi dan pelaporan pengelolaan kas; seperti:', 'perpindahbukukannya dana dari RKUN ke RPKBUN-P dan RP BI; RKUN ke rekening penempatan di Bank Indonesia; dana dari rekening pengelolaan SBN ke RKUN; RKUN ke Reksus;\r\nkartu pengawasan penempatan;\r\nlaporan arus kas UAPBUN AP;\r\nsurat pemindahbukuan dana;\r\nlaporan harian pemindahbukuan penempatan/ investasi yang jatuh tempo; daftar transaksi treasury dealing room harian yang akan jatuh tempo;\r\ndokumen setelmen transaksi treasury dealing room/deal ticket; dokumen transaksi; dan trade confirmation; permintaan otorisasi dan pembatalan transaksi; dan\r\npenerbitan Surat Ketetapan Keterlanjuran Setoran Penerimaan Negara (SKKSPN) atas transaksi tertentu melalui RKUN.', 'Terbatas', 'Eselon IV', NULL, NULL, NULL, NULL),
(667, 'PB2', NULL, NULL, 'PB2', 'PB2', 'primer', 'Sistem Manajemen Investasi', 'Arsip yang berkaitan dengan sistem manajemen investasi.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(668, 'PB2', 'PB2.0', NULL, 'PB2.0', 'PB20', 'sekunder', 'Hubungan Kelembagaan Investasi', 'Arsip yang berkaitan dengan hubungan kelembagaan investasi; seperti:', 'rumusan pengaturan kelembagaan pengelola investasi pemerintah (BLU pengelola dana); bahan/ data tentang kelembagaan badan/ lembaga keuangan investasi lain yang dikelola pemerintah dan swasta baik dalam maupun luar negeri dan hubungannya dengan para pemangku kepentingan; dan\r\npengelolaan investasi oleh operator investasi (BLU pengelola dana); data dan informasi terkait metode penilaian kinerja pengelolaan investasi pada Direktorat Sistem Manajemen Investasi; dan rumusan metode penilaian kinerja pengelolaan investasi pemerintah;', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(669, 'PB2', 'PB2.1', NULL, 'PB2.1', 'PB21', 'sekunder', 'Pengelolaan Risiko dan Penganggaran Investasi', 'Arsip yang berkaitan dengan pengelolaan risiko dan penganggaran investasi.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(670, 'PB2', 'PB2.1', 'PB2.1.0', 'PB2.1.0', 'PB210', 'tersier', 'Pengelolaan Kinerja dan Risiko Investasi', 'Arsip yang berkaitan dengan pengelolaan kinerja; dan risiko investasi; seperti:', 'strategi pengendalian risiko pengelolaan investasi pemerintah; penerusan pinjaman; kredit program dan investasi lainnya;\r\nprofil risiko pengelolaan investasi pemerintah; penerusan pinjaman; kredit program dan investasi lainnya;\r\nhasil pengujian kemampuan prediktif model risiko pengelolaan investasi pemerintah; penerusan pinjaman; kredit program dan investasi lainnya;\r\nhasil monitoring dan evaluasi manajemen risiko pengelolaan investasi pemerintah; penerusan pinjaman; kredit program dan investasi lainnya;\r\nhasil analisis laporan realisasi dan statistik investasi pemerintah; penerusan pinjaman; kredit program; dan investasi lainnya;\r\ndata dan informasi terkait investasi pemerintah; penerusan pinjaman; kredit program; dan investasi lainnya; dan\r\ndokumen penyesuaian lndikasi Kebutuhan Dana (IKD) penerusan pinjaman.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(671, 'PB2', 'PB2.1', 'PB2.1.1', 'PB2.1.1', 'PB211', 'tersier', 'Penganggaran Investasi', 'Arsip yang berkaitan dengan dokumen pendukung proses penganggaran BA BUN 999.03; 999.04; dan 999.07; seperti:', 'usulan indikasi kebutuhan dana/ penyesuaian indikasi kebutuhan dana bendahara umum negara;\r\nusulan revisi DIPA; dan\r\nlaporan hasil reviu APIP K/L sehubungan dengan penyampaian RKA BUN dalam rangka penyusunan DIPA dan revisi DIPA.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(672, 'PB2', 'PB2.2', NULL, 'PB2.2', 'PB22', 'sekunder', 'Investasi Badan Usaha Milik Negara/BUMD Jasa Keuangan', 'Arsip yang berkaitan dengan investasi Badan Usaha Milik Negara (BUMN) dan BUMD Jasa Keuangan; seperti:', 'surat persetujuan dan surat kuasa penandatanganan perjanjian pinjaman; dan surat permohonan pinjaman pemerintah dari BUMN/ PT dan BUMD jasa keuangan bank;\r\nsurat persetujuan tentang pemberian pinjaman dan surat kuasa penandatanganan perjanjian pinjaman dan surat pernyataan ketidakmampuan pembayaran kewajiban yang akan jatuh tempo;\r\nsurat persetujuan penerusan pinjaman dan surat kuasa penandatangan perjanjian penerusan pinjaman; dan surat permintaan penerusan pinjaman beserta lampirannya dari BUMN;\r\nsurat penetapan tingkat bunga pinjaman kepada BUMN/PT dan BUMD Jasa keuangan bank; dan permintaan data kepada Bank Indonesia dan Direktorat Jenderal Pembiayaan dan Pengelolaan Risiko;\r\nlaporan hasil monitoring dan evaluasi atas pinjaman pemerintah dan penerusan pinjaman kepada BUMN/PT dan BUMD jasa keuangan bank selama periode pembangunan; dan laporan perkembangan dan permasalahan pelaksanaan kegiatan yang dibiayai dari dana pinjaman baik secara teknis; administratif Rencana Penggunaan Dana Pinjaman (RPDP); Laporan Penggunaan Dana Pinjaman (LPDP); kontrak/ addendum; surat perintah kerja; berita acara kemajuan fisik kegiatan; berita acara serah terima kegiatan; rekening koran maupun keuangan; serta dokumen pendukung BUMN/PT dan BUMD Jasa keuangan; Rencana Kerja Anggaran Perusahaan (RKAP); laporan keuangan Audited dan laporan hasil evaluasi kinerja;\r\npersetujuan/penolakan untuk penerbitan perubahan NPPP;\r\nrekomendasi persetujuan restrukrisasi kepada pihak-pihak terkait; dan surat permohonan restrukturisasi berikut lampirannya dari BUMN/PT;\r\nlaporan evaluasi kinerja BUMN/PT dan BUMD Jasa keuangan bank pasca penyelesaian piutang negara dan rekomendasi atas hasil evaluasi; kartu pengawasan investasi BUMN/PT dan BUMD jasa keuangan bank;\r\nlaporan evaluasi kinerja BUMN/PT dan BUMD Jasa keuangan bank pasca penyelesaian piutang negara dan rekomendasi atas hasil evaluasi; kartu pengawasan investasi BUMN/PT dan BUMD jasa keuangan bank;\r\npermintaan kelengkapan dokumen data kepada BUMN/PT; dan\r\nanalisis pelaksanaan investasi BUMN/PT; laporan perkembangan investasi BUMN/PT.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(673, 'PB2', 'PB2.3', NULL, 'PB2.3', 'PB23', 'sekunder', 'Investasi Pemerintah Daerah/Badan Usaha Milik Daerah', 'Arsip yang berkaitan dengan investasi Pemda/BUMD; seperti:', 'laporan monitoring dan evaluasi penerusan pinjaman kepada Pemda/BUMD selama periode pembangunan; rekapitulasi penarikan penerusan pinjaman; dan laporan pelaksanaan dari debitur; \r\ntindak lanjut hasil monitoring dan evaluasi perkembangan dan permasalahan pelaksanaan kegiatan yang dibiayai dari dana pinjaman dan kemampuan Pemda/ BUMD mengembalikan pinjaman; rekapitulasi penarikan penerusan pinjaman; laporan pelaksanaan dari debitur dan berita acara rekonsiliasi pinjaman; \r\nanalisis pelaksanaan investasi Pemda/ BUMD; laporan perkembangan investasi Pemda/BUMD; usulan Pemda/BUMD; dan hasil penelitian dan/ atau rekomendasi dari unit teknis terkait; \r\npenetapan persetujuan permohonan penerusan pinjaman Pemda/BUMD disertai dokumen pendukung;\r\nrekomendasi penyelesaian sengketa/perselisihan pelaksanaan perjanjian pinjaman;\r\npenetapan percepatan pelunasan pembayaran dan penyelesaian piutang negara beserta dokumen pendukung;\r\npenetapan permohonan persetujuan penyelesaian piutang negara pada pemda/BUMD dan dokumen pendukung terkait;\r\npenetapan permohonan debt swap; reviu debt swap dan penagihan kekurangan debt swap;\r\nrekomendasi penghapusan piutang negara pada pemda/BUMD; dan\r\npelaksanaan pemotongan DAU/DBH akibat tunggakan pembayaran piutang negara.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(674, 'PB2', 'PB2.4', NULL, 'PB2.4', 'PB24', 'sekunder', 'Kredit Program dan Investasi Lainnya', 'Arsip yang berkaitan dengan kegiatan pelaksanaan kredit program dan investasi lainnya.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(675, 'PB2', 'PB2.4', 'PB2.4.0', 'PB2.4.0', 'PB240', 'tersier', 'Persetujuan/Penolakan Kredit Program dan Investasi Lainnya', 'Arsip yang berkaitan dengan kegiatan persetujuan/penolakan kredit program dan investasi lainnya; seperti:', 'persetujuan/penetapan skema pembiayaan kredit program; dan usulan kementerian teknis; LKP; lembaga penjaminan; dan instansi lainnya; \r\npersetujuan/penolakan penunjukan LKP kredit program; permohonan penunjukan sebagai LKP kredit program; rekomendasi dari kementerian teknis; salinan anggaran dasar LKP/BUMN/P; salinan laporan keuangan LKP/BUMN/P dua tahun terakhir; persetujuan dari dewan komisaris LKP/BUMN/P; surat keterangan dari BI terkait tingkat kesehatan LKP/BUMN/P; dan data perkembangan penyaluran kredit program;\r\npersetujuan/penolakan atas permohonan pengurangan plafon atau permohonan percepatan pembayaran kembali pinjaman kepada LKP/BUMN/P kredit program; permohonan pengurangan platform/percepatan pembayaran kembali pinjaman kepada LKP/BUMN/P kredit program; dan perjanjian kerjasama dengan LKP /LPK/BUMN-P terkait; \r\npermohonan audit kelayakan penyelesaian piutang negara pada petani kepada auditor eksternal/ internal; \r\nrekomendasi penyelesaian piutang negara kepada Menteri Keuangan; dan\r\nanalisis pelaksanaan kredit program dan investasi lainnya; laporan perkembangan pelaksanaan kredit program dan investasi lainnya; usulan kementerian teknis; LKP; dan instansi lainnya; peraturan terkait kredit program; dan data dan/ atau bahan masukan dari unit teknis terkait.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(676, 'PB2', 'PB2.4', 'PB2.4.1', 'PB2.4.1', 'PB241', 'tersier', 'Laporan Pelaksanaan  Kredit Program dan Investasi Lainnya', 'Arsip yang berkaitan dengan kegiatan laporan pelaksanaan kredit program dan investasi lainnya; seperti:', 'laporan pelaksanaan monitoring dan evaluasi pelaksanaan kredit program; dan rekomendasi tindak lanjut; laporan realisasi penyaluran kredit program oleh bank pelaksana/lembaga keuangan bukan bank pelaksana kredit program; perjanjian kerjasama dengan LKP/LPK/BUMN/P terkait; dan data dan dokumen sehubungan dengan penyaluran kredit program; dan\r\n\r\nlaporan perkembangan pelaksanaan kredit program; laporan berkala penyaluran dan pengembalian kredit program dan laporan lainnya dari LKP dan LPK; dan perjanjian kredit program dengan LKP dan LPK;', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(677, 'PB2', 'PB2.5', NULL, 'PB2.5', 'PB25', 'sekunder', 'Tingkat Bunga Kredit', 'Arsip yang berkaitan dengan kegiatan penetapan tingkat bunga kredit; seperti:', 'tingkat bunga Kredit Ketahanan Pengadaan Energi (KKP-E); \r\ntingkat bunga Pengembangan Energi Nabati dan Revitalisasi Perkebunan (KPEN-RP); \r\ntingkat bunga Kredit Pemberdayaan Pengusaha Nangroe Aceh Darussalam dan Nias (KPP NAD Nias); \r\ntingkat bunga Kredit Usaha Pembibitan Sapi (KUPS); \r\ntingkat bunga Skema Subsidi Resi Gudang (SSRG); dan \r\ntingkat bunga Kredit Usaha Mikro dan Kecil (KUMK).', 'Terbuka', 'Umum', NULL, NULL, NULL, NULL),
(678, 'PB3', NULL, NULL, 'PB3', 'PB3', 'primer', 'Pengelolaan Keuangan Badan Layanan Umum (BLU)', 'Arsip yang berkaitan dengan kegiatan pembinaan pengelolaan keuangan BLU.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(679, 'PB3', 'PB3.0', NULL, 'PB3.0', 'PB30', 'sekunder', 'Penetapan Status Badan Layanan Umum', 'Arsip yang berkaitan dengan kegiatan penetapan status BLU.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(680, 'PB3', 'PB3.0', 'PB3.0.0', 'PB3.0.0', 'PB300', 'tersier', 'Pembentukan dan Penetapan BLU', 'Arsip yang berkaitan dengan pembentukan dan penetapan BLU; seperti:', 'usulan pembentukan BLU dari K/L terkait;\r\npenilaian penetapan BLU;\r\nberita acara penilaian penetapan BLU;\r\nrekomendasi/usulan penetapan BLU kepada Menteri Keuangan; dan\r\npenetapan BLU/penolakan status BLU.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(681, 'PB3', 'PB3.0', 'PB3.0.1', 'PB3.0.1', 'PB301', 'tersier', 'Pencabutan status BLU', 'Arsip yang berkaitan dengan pencabutan status BLU yang berasal dari hasil monitoring terhadap BLU dan permintaan K/L.', NULL, 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(682, 'PB3', 'PB3.1', NULL, 'PB3.1', 'PB31', 'sekunder', 'Tarif; Remunerasi dan Informasi', 'Arsip yang berkaitan dengan kegiatan penyusunan tarif; remunerasi dan informasi BLU.', NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(683, 'PB3', 'PB3.1', 'PB3.1.0', 'PB3.1.0', 'PB310', 'tersier', 'Pengusulan Tarif Layanan BLU', 'Arsip yang berkaitan dengan penyusunan tarif layanan BLU; seperti:', 'rancangan tarif layanan BLU; usulan tarif Layanan BLU dan dokumen usulan tarif layanan BLU;\r\npengembalian atau penolakan usulan tarif layanan BLU dan permintaan kelengkapan usulan tarif layanan BLU; pengembalian atau penolakan usulan tarif layanan BLU; permintaan kelengkapan usulan tarif layanan BLU dan dokumen usulan tarif layanan BLU; dan\r\nhasil kajian usulan tarif layanan BLU dan dokumen usulan tarif layanan BLU.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(684, 'PB3', 'PB3.1', 'PB3.1.1', 'PB3.1.1', 'PB311', 'tersier', 'Penetapan Tarif Layanan BLU', 'Arsip yang berkaitan dengan penetapan tarif layanan satuan kerja BLU.', NULL, 'Terbuka', 'Umum', NULL, NULL, NULL, NULL),
(685, 'PB3', 'PB3.1', 'PB3.1.2', 'PB3.1.2', 'PB312', 'tersier', 'Remunerasi BLU', 'Arsip yang berkaitan dengan penyusunan remunerasi BLU; seperti:', 'rancangan remunerasi BLU; usulan remunerasi BLU dan dokumen usulan remunerasi BLU;\r\npengembalian atau penolakan usulan remunerasi BLU dan permintaan kelengkapan usulan remunerasi BLU; pengembalian atau penolakan usulan remunerasi BLU; permintaan kelengkapan usulan remunerasi BLU dan dokumen usulan remunerasi BLU;\r\nhasil kajian usulan remunerasi BLU; dan\r\npenetapan remunerasi BLU dan dokumen usulan remunerasi BLU.', 'Terbatas', 'Eselon III', NULL, NULL, NULL, NULL),
(686, 'PB3', 'PB3.2', NULL, 'PB3.2', 'PB32', 'sekunder', 'Pembinaan Pengelolaan Keuangan Badan Layanan Umum', 'Arsip yang berkaitan dengan kegiatan pembinaan pengelolaan keuangan BLU.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
