(754, 'PB8', 'PB8.0', NULL, 'PB8.0', 'PB80', 'sekunder', 'Setelmen Investasi', 'Arsip yang berkaitan dengan kegiatan setelmen investasi; seperti:', 'dokumen lelang penempatan dana PIP;\r\ndokumen rekonsiliasi antara PIP dengan pihak LKBB per bulan; dan\r\ndokumen rekonsiliasi antara PIP dengan pihak perbankan per triwulan', 'Terbatas', 'Eselon III/setingkatnya', NULL, NULL, NULL, NULL),
(755, 'PB8', 'PB8.1', NULL, 'PB8.1', 'PB81', 'sekunder', 'Kerjasama Pendanaan', 'Arsip yang berkaitan dengan kegiatan kerjasama pendanaan; seperti:', 'nota kesepakatan MoU (Memorandum of Understanding) antara PIP dengan pemda/pihak lainnya; dan \r\nPKS (Perjanjian Kerja Sama) antara PIP dengan pemda/pihak lainnya.', 'Terbatas', 'Eselon III/setingkatnya', NULL, NULL, NULL, NULL),
