(764, 'PB9', 'PB9.1', 'PB9.1.0', 'PB9.1.0', 'PB910', 'tersier', 'Penyaluran Dana Bergulir', 'Arsip yang berkaitan dengan kegiatan penyaluran dana bergulir; seperti:', 'dokumen pokok dan dokumen penunjang jaminan penyaluran dana bergulir;\r\nproposal permohonan penyaluran dana bergulir;\r\nrisalah komite pembiayaan;\r\nsurat perintah penyaluran dana bergulir; dan\r\ndokumen penyaluran dana bergulir yang dipersamakan.', 'Terbatas', 'Eselon III/setingkatnya', NULL, NULL, NULL, NULL),
(765, 'PB9', 'PB9.1', 'PB9.1.1', 'PB9.1.1', 'PB911', 'tersier', 'Penyaluran Dana Program', 'Arsip yang berkaitan dengan kegiatan penyaluran dana program; seperti:', 'proposal permohonan penyaluran dana program;\r\nlaporan pertanggungjawaban penyaluran dana program;\r\nsurat perintah penyaluran dana program; dan\r\ndokumen penyaluran dana program yang dipersamakan.', 'Terbatas', 'Eselon III/setingkatnya', NULL, NULL, NULL, NULL),
